import React, { Component } from "react";
import NewsItems from "./NewsItems";

export default class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page:1
    };
  }

  async componentDidMount() {
    let url = "https://newsapi.org/v2/everything?q=tesla&from=2023-08-08&sortBy=publishedAt&apiKey=21c383857cf74a4e939e85289499fef4";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles : parsedData.articles})
  }

  render() {
    return (
      <>
        <div className="container my-5">
          <h1 className="m-3">Newsman Headlines</h1>
          <div className="row my-5">
            {this.state.articles.map((element) => {
              return <div className="col-md-3" key={element.id}>
                <NewsItems
                  title={element.title?element.title.slice(0,45):""}
                  description={element.description?element.description.slice(0,85):""}
                  newsUrl={element.url}
                  imageUrl={element.urlToImage}
                />
              </div>;
            })}
          </div>
          <div className="container d-flex justify-content-between">
          <button type="button" class="btn btn-sm btn-dark">&larr; Previous</button>
          <button type="button" class="btn btn-sm btn-dark">Next &rarr;</button>
          </div>
        </div>
      </>
    );
  }
}
